import React, { useRef, useState } from "react";
import axios from "axios";

const WebcamCapture = () => {
  const videoRef = useRef(null);
  const [email, setEmail] = useState("");
  const [status, setStatus] = useState("");
  const [emotion, setEmotion] = useState("");

  const startWebcam = () => {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
      })
      .catch((err) => console.error("Error al acceder a la cámara: ", err));
  };

  const captureAndSend = async () => {
    const canvas = document.createElement("canvas");
    const context = canvas.getContext("2d");
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    const blob = await new Promise((resolve) => canvas.toBlob(resolve, "image/jpeg"));
    const formData = new FormData();
    formData.append("email", email);
    formData.append("image", blob);

    try {
      const response = await axios.post("http://127.0.0.1:5000/authenticate", formData);
      setStatus("Autenticación Exitosa");
      setEmotion(`Emoción Detectada: ${response.data.emotion}`);
    } catch (error) {
      setStatus("Error en la autenticación");
    }
  };

  return (
    <div>
      <h1>Validación de Identidad</h1>
      <input
        type="email"
        placeholder="Correo del estudiante"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <div>
        <video ref={videoRef} autoPlay muted style={{ width: "300px" }}></video>
      </div>
      <button onClick={startWebcam}>Iniciar Cámara</button>
      <button onClick={captureAndSend}>Validar Identidad</button>
      <p>{status}</p>
      <p>{emotion}</p>
    </div>
  );
};

export default WebcamCapture;
